  background-color: rgb(72, 77, 91);
  display: block;
  margin: 0 calc(-1 * var(--ifm-pre-padding));
  padding: 0 var(--ifm-pre-padding);
}

.navbar .navbar__brand > strong {
  flex-shrink: 0;
  max-width: 100%;
}

